  // TODO

  return (
    <div>
    <table className = "board">
      <tbody>
        {board.map( (row, y) => (
          <tr>
         {row.map((isLit,x) => (
          <Cell
            isLit= {isLit}
            flipCellsAroundMe = {() => flipCellsAround(`${y}-${x}`)}


          />
         )

         )}

        </tr>
        )

        )}

      </tbody>
    </table>
          <button className = 'button' onClick = {() => randomizeAgain(board)}>Restart</button>
    </div>
  )
}

export default Board;
